import React, { Component } from "react"
import DataManager from "../../modules/DataManager"

export default class EditMedicationForm extends Component {
    state = {
        name:"",
        date:"",
        time:"",
        quantity:"",
        frequency:"",
        userId:""
    }


    handleFieldChange = evt => {
        const stateToChange= {}
        stateToChange[evt.target.id] = evt.target.value
        this.setState(stateToChange)
    }

    editMedication = evt => {
        evt.preventDefault();
        const existingMedication = {
            name: this.state.name,
            date: this.state.date,
            time: this.state.time,
            quantity: this.state.quantity,
            frequency: this.state.frequency,
            userId: Number(sessionStorage.getItem("User"))
        }
        this.props.editMedication(this.props.match.params.id,existingMedication)
        .then(()=> this.props.history.push("/meds"))
    }
    render () {
        return (
            <>
             {console.log("NAME:",this.props.medication.name)}
            <form>
                <h2>Edit Medication</h2>
                <div>
                    <label htmlFor="name">Name</label>
                    <input type="text" onChange={this.handleFieldChange} id="name" placeholder={this.props.medications.name} />
                </div>
            </form>

            </>
        )
    }
}
