import React, { Component } from "react"
import DataManager from "../../modules/DataManager"
import { Link } from "react-router-dom"
export default class NoteCard extends Component {

    state = {
        userId: "",
        doctorId: "",
        time: "",
        date: "",
        reason: "",
        note: "",
        timestamp: "",
        audio: "",
        photo: "",
        id: ""
    }

    componentDidMount() {
        DataManager.getById(this.props.match.params.id, "appointments").then(appointment => {
            console.log("appointment", appointment)
            this.setState({
                userId: appointment.userId,
                doctorId: appointment.doctorId,
                time: appointment.time,
                date: appointment.date,
                reason: appointment.reason,
                note: appointment.note,
                timestamp: appointment.timestamp,
                audio: appointment.audio,
                photo: appointment.photo,
                id: appointment.id
            })
        })
    }



    render () {
        let reader = new window.FileReader()
        console.log("BLOB",this.state.audio)
        reader.readAsDataURL(`blob:http://localhost:3000/184fa524-529a-4853-bf39-b054d2e59534`);
        reader.onloadend = function () {
            let base64data = reader.result;
            console.log(base64data);
        }
        if (this.state.audio) {
        return (
            <>
            <div>
                <p>{this.state.note}</p>
                <Link to={`/note/edit/${this.state.id}`}>Edit</Link>
            </div>
            <figcaption>Listen:</figcaption>
        <audio
        controls
        src="blob:http://localhost:3000/f162df71-d738-45ae-ba1b-a872fb254a48">
            Your browser does not support the
            <code>audio</code> element.
    </audio>

            <button type="button" onClick={()=> this.props.history.push("/appointments")}>Back</button>

            </>
        )
        }else {
            return (
                <>
                </>
            )
        }}
}
