import React, { Component } from "react"
import AppointmentCard from "./AppointmentCard"
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import "./Appointment.css"
import DoctorIcon from "../../images/appointments/doctor.png"
import NewDoctorForm from "../doctors/NewDoctorForm"


export default class AppointmentList extends Component {
    state = {
        modal: false
        // ,
        // name: "",
        // location: ""
    }

    handleFieldChange = evt => {
        const stateToChange = {};
        stateToChange[evt.target.id] = evt.target.value;
        this.setState(stateToChange)
    }

    // addDoctor = evt => {
    //     evt.preventDefault();
    //     const newDoctor = {
    //         name: this.state.name,
    //         location: this.state.location,
    //         userId: Number(sessionStorage.getItem("User"))
    //     }
    //     this.props.postNewDoctor(newDoctor)
    //     this.toggle()
    // }

    toggle= () => {
        this.setState(prevState => ({
          modal: !prevState.modal
        }));
      }

    render() {
        console.log("APT PROPS",this.state)
         const sortedApts = this.props.appointments.sort(function (aptA, aptB) {
            return new Date (aptA.date) - new Date(aptB.date)
        })
        return (
            <>
                <div className="appointmentList">
                <img src={DoctorIcon} alt="doctor icon" width="50" height="50"/>
                    <h2>Appointment List</h2>

                {/* <button type="submit" onClick={()=> {
                    this.props.history.push("/doctor/new")
                }} >
                Add Doctor
                </button> */}
                </div>

                <Button onClick={this.toggle} size="sm"> {this.props.buttonLabel}
                             <h1 className="add-h1" id="add-doctor-button">
                               Add Doctor
                            </h1>
                        </Button>



                        <div className="centerModal">
                <Modal isOpen={this.state.modal} className="modal-sm" toggle={this.toggle} >
                <ModalHeader toggle={this.toggle}>Add Doctor</ModalHeader>
                <ModalBody >
                <form>
                <h2>Add New Doctor</h2>
                {/* <div>
                    <label htmlFor="doctor">Name</label>
                    <input type="text" onChange={this.handleFieldChange} id="name"/>
                    <label htmlFor="location">Location</label>
                    <input type="text" onChange={this.handleFieldChange} id="location"/>
                </div> */}
                <NewDoctorForm />
                </form>
                </ModalBody>
                <ModalFooter>
                    <Button color={this.state.location ?"success" : "primary"} onClick={this.addDoctor}>Save</Button>{' '}
                    <Button color="secondary" onClick={this.toggle}>Cancel</Button>
                </ModalFooter>
                </Modal>
                </div>

                <section className="appointment-list">
                    {sortedApts.map(appointment =>
                    (
                        <div key={appointment.id}>
                        <AppointmentCard key={appointment.id} appointment={appointment} {...this.props} />
                        </div>

                    ))}
                </section>

                <Button color="success" type="submit" id="add-appointment-button" onClick={() => {
                        this.props.history.push("/appointment/new")
                    }} >
                        Add
                </Button>

            </>
        )
    }
}
